    <div className="page-wrap">
      <Header buttonClick={toggleModal}/>
      <Modal open={showModal}/>
      <main className="main">
        <div className="wrap">
          <ul>
            {getUncheckedItems}
          </ul>
          <Archive/>
        </div>
